const API_URL = 'https://api.giphy.com/v1/gifs/search?api_key=dIJrma20pSU6ymMwWnDbiaT7NFHeAGVa&q=panda&limit=10&offset=0&rating=G&lang=en'

export default function getGifs () {
    return fetch(API_URL)
    .then(res => res.json())
    .then(response => {
      const {data = []} = response
      if(Array.isArray(data)) {
        const gifs= data.map(image => image.images.downsized_medium.url)
        return gifs
        }
    })  
}
