const API_KEY = 'dIJrma20pSU6ymMwWnDbiaT7NFHeAGVa'

export default function getGifs ({keyword = 'messi'} = {}) {

    const API_URL = `https://api.giphy.com/v1/gifs/search?api_key=${API_KEY}&q=${keyword}&limit=10&offset=0&rating=G&lang=en`

    return fetch(API_URL)
    .then(res => res.json())
    .then(response => {
      const {data = []} = response
      if(Array.isArray(data)) {
        const gifs= data.map(image => image.images.downsized_medium.url)
        return gifs
        }
    })  
}
