import './App.css';
import React, {useState, useEffect} from 'react';

const API_URL = 'https://api.giphy.com/v1/gifs/search?api_key=dIJrma20pSU6ymMwWnDbiaT7NFHeAGVa&q=panda&limit=10&offset=0&rating=G&lang=en'


function App() {
  const [gifs, setGifs] = useState('')

  useEffect(() => {
    fetch(API_URL)
    .then(res=> res.json())
    .then(data => setGifs(  data))
  }, [])

  return (
    <div className="App">
      <section className="App-content">
        {
        gifs.map(singleGif => <img alt='Gif of Panda' src={singleGif} />)
        }
      </section>
    </div>
  );
}

export default App;
